// src/App.js

import React, { useState } from 'react';
import Chart from 'chart.js/auto';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('Player Stats');

  return (
    <div className="App">
      <header>
      <img src="app_ui/public/IPL_logo.jpeg" alt="IPL Logo" width="30" height="20"/>
        <h1>IPL Analytics</h1>
      </header>
      <div className="tabs">
        <button
          className={activeTab === 'Player Stats' ? 'active' : ''}
          onClick={() => setActiveTab('Player Stats')}
        >
          Player Stats
        </button>
        <button
          className={activeTab === 'Team Stats' ? 'active' : ''}
          onClick={() => setActiveTab('Team Stats')}
        >
          Team Stats
        </button>
        <button
          className={activeTab === 'Match Stats' ? 'active' : ''}
          onClick={() => setActiveTab('Match Stats')}
        >
          Match Stats
        </button>
      </div>
      <div className="visualization-grids">
        {/* Render the appropriate component based on the active tab */}
        {activeTab === 'Player Stats' && <PlayerStatsGrid />}
        {activeTab === 'Team Stats' && <TeamStatsGrid />}
        {activeTab === 'Match Stats' && <MatchStatsGrid />}
      </div>
    </div>
  );
}

export function PlayerStatsGrid() {
  // Generate random data for the bar charts
  const generateRandomData = () => {
    return Array.from({ length: 5 }, () => Math.floor(Math.random() * 100));
  };

  // Create a function to render a bar chart
  const renderBarChart = (canvas, data) => {
    const ctx = canvas.getContext('2d');
    new Chart(ctx, {
      type: 'bar',
      data: {
        labels: ['Category 1', 'Category 2', 'Category 3', 'Category 4', 'Category 5'],
        datasets: [
          {
            label: 'Data',
            data: data,
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 1,
          },
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    });
  };

  return (
    <div className="player-stats-grid">
      {[0, 1, 2, 3].map((gridIndex) => (
        <div key={gridIndex} className="grid-item">
          <canvas
            id={`chart-${gridIndex}`}
            width="400"
            height="200"
            ref={(canvas) => {
              // Render the chart on the canvas element
              if (canvas) {
                const data = generateRandomData();
                renderBarChart(canvas, data);
              }
            }}
          ></canvas>
        </div>
      ))}
    </div>
  );
}

function TeamStatsGrid() {
  // Implement the Team Stats grid here
}

function MatchStatsGrid() {
  // Implement the Match Stats grid here
}

export default App;
